import React, { useState } from "react";

const Images = () => {
  const [address, setAddress] = useState("");
  const [imageUrl, setImageUrl] = useState(null);
  const [error, setError] = useState("");

  const handleInputChange = (e) => {
    setAddress(e.target.value);
  };

  const fetchHouseImage = async () => {
    if (!address.trim()) {
      setError("Please enter a valid address.");
      return;
    }

    setError("");
    const apiKey = "AIzaSyCDm-kHtEIsMQMo_VkGQ3pWDz_eu7S9O-0";

    try {
      // Step 1: Get the Place ID for the given address
      const placeSearchUrl = `https://maps.googleapis.com/maps/api/place/findplacefromtext/json?input=${encodeURIComponent(
        address
      )}&inputtype=textquery&fields=place_id&key=${apiKey}`;

      const placeResponse = await fetch(placeSearchUrl);
      const placeData = await placeResponse.json();

      if (placeData.status !== "OK" || !placeData.candidates.length) {
        setError("No place found for the given address.");
        return;
      }

      const placeId = placeData.candidates[0].place_id;

      // Step 2: Fetch the photo reference using the Place Details API
      const placeDetailsUrl = `https://maps.googleapis.com/maps/api/place/details/json?place_id=${placeId}&fields=photos&key=${apiKey}`;
      const detailsResponse = await fetch(placeDetailsUrl);
      const detailsData = await detailsResponse.json();

      if (
        detailsData.status !== "OK" ||
        !detailsData.result.photos ||
        !detailsData.result.photos.length
      ) {
        setError("No photos available for this address.");
        return;
      }

      const photoReference = detailsData.result.photos[0].photo_reference;

      // Step 3: Use the photo reference to get the image URL
      const photoUrl = `https://maps.googleapis.com/maps/api/place/photo?maxwidth=600&photoreference=${photoReference}&key=${apiKey}`;
      setImageUrl(photoUrl);
    } catch (err) {
      console.error(err);
      setError("Error fetching house image. Please try again.");
    }
  };

  return (
    <div style={{ fontFamily: "Arial, sans-serif", textAlign: "center", padding: "20px" }}>
      <h1>House Picture Viewer</h1>
      <input
        type="text"
        placeholder="Enter an address"
        value={address}
        onChange={handleInputChange}
        style={{
          padding: "10px",
          width: "300px",
          marginBottom: "10px",
          border: "1px solid #ccc",
          borderRadius: "5px",
        }}
      />
      <br />
      <button
        onClick={fetchHouseImage}
        style={{
          padding: "10px 20px",
          backgroundColor: "#007BFF",
          color: "white",
          border: "none",
          borderRadius: "5px",
          cursor: "pointer",
        }}
      >
        Get Picture
      </button>
      {error && <p style={{ color: "red", marginTop: "10px" }}>{error}</p>}
      {imageUrl && (
        <div style={{ marginTop: "20px" }}>
          <h2>House Image:</h2>
          <img src={imageUrl} alt="Closer View of House" style={{ maxWidth: "100%", borderRadius: "10px" }} />
        </div>
      )}
    </div>
  );
};

export default Images;
